import { CART_ADD_ITEM, CART_REMOVE_ITEM} from '../constants/cartConstants';

const cartReducer = (state = { cartItems: []}, {type, payload}) => {
  switch(type) {
    case CART_ADD_ITEM:
      return state;
    case CART_REMOVE_ITEM:
      return state;
    default:
      return state
  }
}


export default cartReducer;
